package jetbrains.mps.tool.common.util;

/*Generated by MPS */

import jetbrains.mps.project.Project;
import java.io.File;
import java.io.IOException;
import org.apache.log4j.Level;
import org.apache.log4j.Logger;
import org.apache.log4j.LogManager;

public class ProjectUtil {
  public static boolean projectHasPath(Project project, File path) {
    File projectFile = project.getProjectFile();
    if (projectFile == null) {
      return false;
    }
    try {
      String myProjectPath = projectFile.getCanonicalPath();
      String newProjectPath = path.getCanonicalPath();
      return myProjectPath.equals(newProjectPath);
    } catch (IOException e) {
      if (LOG.isEnabledFor(Level.ERROR)) {
        LOG.error("Cannot access the project file in container", e);
      }
    }
    return false;
  }
  protected static Logger LOG = LogManager.getLogger(ProjectUtil.class);
}
