import jetbrains.mps.smodel.SModelReference;
import jetbrains.mps.vfs.IFile;
import jetbrains.mps.smodel.SModelFqName;
import jetbrains.mps.smodel.SModelId;

public class TextPersistenceUtil {
  public TextPersistenceUtil() {
  }

  public static SModelReference refByFile(IFile file) {
    String filename = file.getName();
    String modelName = filename.substring(0, filename.length() - 4);
    return new SModelReference(new SModelFqName(modelName, "text"), SModelId.foreign(file.getPath()));
  }
}
