package jetbrains.mps.ide.migration.wizard;

/*Generated by MPS */

import com.intellij.openapi.project.Project;
import javax.swing.JPanel;
import java.awt.BorderLayout;
import javax.swing.BorderFactory;
import javax.swing.JTextPane;
import javax.swing.event.HyperlinkListener;
import javax.swing.event.HyperlinkEvent;
import com.intellij.ide.BrowserUtil;
import java.awt.Dimension;

public class MigrationsFinishedStep extends MigrationStep {
  public static final String ID = "finished";
  public MigrationsFinishedStep(Project project) {
    super(project, "Migration Finished", ID);
    createComponent();
  }
  @Override
  protected final void createComponent() {
    super.createComponent();
    JPanel infoHolder = new JPanel(new BorderLayout());
    infoHolder.setBorder(BorderFactory.createEmptyBorder(2, 2, 2, 2));
    JTextPane info = new JTextPane();
    info.setContentType("text/html");
    info.setText("Congratulations! Migration has completed successfully.<br><br>Your project files have been upgraded to be used with the latest version of MPS.<br><br>The wizard can now be closed and your project will be reloaded.");
    info.setEditable(false);
    info.addHyperlinkListener(new HyperlinkListener() {
      public void hyperlinkUpdate(HyperlinkEvent e) {
        if (e.getEventType() == HyperlinkEvent.EventType.ACTIVATED) {
          BrowserUtil.launchBrowser(e.getURL().toString());
        }
      }
    });
    info.setFocusable(false);
    info.setBorder(BorderFactory.createLoweredBevelBorder());
    info.setPreferredSize(new Dimension(300, 220));
    infoHolder.add(info, BorderLayout.CENTER);
    myComponent.add(infoHolder, BorderLayout.CENTER);
  }
  @Override
  public Object getPreviousStepId() {
    return null;
  }
  @Override
  public Object getNextStepId() {
    return null;
  }
  @Override
  public boolean canBeCancelled() {
    return false;
  }
}
