package jetbrains.mps.nanoc.debug;

/*Generated by MPS */

import org.apache.commons.logging.Log;
import org.apache.commons.logging.LogFactory;
import com.intellij.execution.process.ProcessHandler;
import java.io.OutputStreamWriter;

public class ProcessUtil {
  protected static Log log = LogFactory.getLog(ProcessUtil.class);

  public ProcessUtil() {
  }

  public static void inputWithFlush(ProcessHandler process, String text) {
    try {
      OutputStreamWriter outputStreamWriter = new OutputStreamWriter(process.getProcessInput());
      outputStreamWriter.append(text);
      outputStreamWriter.flush();
    } catch (Throwable t) {
      if (log.isErrorEnabled()) {
        log.error("", t);
      }
    }
  }
}
