package jetbrains.mps.testbench.junit.runners;

/*Generated by MPS */

import java.util.List;
import org.junit.runner.Runner;
import org.junit.runner.notification.RunNotifier;

/**
 * Use this type @RunWith() carefully. Better specify project with MPSOpenProjectSuite (but i'm not sure that MPSOpenProjectSuite works).
 */
public class ParameterizedMpsTest extends WatchingParameterized {
  private final List<Runner> children;


  public ParameterizedMpsTest(Class<?> clazz) throws Throwable {
    super(initEnv(clazz));
    children = MakeRunner.withMakeRunner(getTestClass(), super.getChildren());
  }

  /**
   * Hack for being executed before Parameterized constructor
   */
  private static Class<?> initEnv(Class<?> clazz) {
    MpsTestsSupport.initEnv(true);
    MpsTestsSupport.loadAllModulesIntoRepository();

    return clazz;
  }

  @Override
  protected void runChild(Runner runner, RunNotifier notifier) {
    super.runChild(runner, notifier);
    if (runner == children.get(children.size() - 1)) {
      MpsTestsSupport.disposeEnv();
    }
  }

  @Override
  protected List<Runner> getChildren() {
    return children;
  }
}
