  @Nullable
  public static Runnable addImportsWithDialog(final SModel targetModel, final Set<SModuleReference> necessaryLanguages, final Set<SModelReference> necessaryImports, final IOperationContext context) {
    if (targetModel.getModule() == null) {
      return null;
    }
    final List<SModuleReference> additionalLanguages = new ArrayList<SModuleReference>();
    final List<SModelReference> additionalModels = new ArrayList<SModelReference>();
    ModelAccess.instance().runReadAction(new Runnable() {
      @Override
      public void run() {
        List<SModelReference> allImportedModels = new ArrayList<SModelReference>();
        for (SModel sm : SModelOperations.allImportedModels(targetModel)) {
          allImportedModels.add(sm.getReference());
        }
        for (SModelReference modelReference : necessaryImports) {
          if (modelReference != null && !((allImportedModels.contains(modelReference))) && !((targetModel.getReference().equals(modelReference)))) {
            additionalModels.add(modelReference);
          }
        }
        necessaryImports.retainAll(additionalModels);
        for (SModuleReference moduleReference : necessaryLanguages) {
          if (!(SModelOperations.hasLanguage(targetModel, moduleReference))) {
            additionalLanguages.add(moduleReference);
          }
        }
        necessaryLanguages.retainAll(additionalLanguages);
      }
    });
    if (necessaryImports.isEmpty() && necessaryLanguages.isEmpty()) {
      return null;
    }

    AddRequiredImportsDialog dialog = new AddRequiredImportsDialog(ProjectHelper.toIdeaProject(context.getProject()), necessaryImports.toArray(new SModelReference[necessaryImports.size()]), necessaryLanguages.toArray(new SModuleReference[necessaryLanguages.size()]));
    dialog.show();
    if (dialog.isOK()) {
      return addImports(context.getProject(), targetModel, dialog.getSelectedLanguages(), dialog.getSelectedImports());
    } else {
      return null;
    }
  }
  @Nullable
  public static Runnable addImportsWithDialog(PasteNodeData pasteNodeData, SModel targetModel, IOperationContext context) {
    // shows dialog if necessary and pasted nodes were taken not from the same model 
    SModelReference oldModel = pasteNodeData.getSourceModel();
    // no dialog if copying from the same model 
    if (oldModel != null && jetbrains.mps.lang.smodel.generator.smodelAdapter.SModelOperations.getModelName(targetModel).equals(oldModel.getModelName())) {
      return null;
    }

    return CopyPasteUtil.addImportsWithDialog(targetModel, pasteNodeData.getNecessaryLanguages(), pasteNodeData.getNecessaryModels(), context);
  }
  private static Runnable addImports(Project p, final SModel targetModel, @NotNull final SModuleReference[] requiredLanguages, @NotNull final SModelReference[] requiredImports) {
    if (requiredLanguages.length == 0 && requiredImports.length == 0) {
      return null;
    }

    return new Runnable() {
      @Override
      public void run() {
        //  model properties 
        for (SModelReference imported : requiredImports) {
          ((SModelInternal) targetModel).addModelImport(imported, false);
        }
        for (SModuleReference language : requiredLanguages) {
          ((SModelInternal) targetModel).addLanguage(language);
        }
        //  model's module properties 
        SModule targetModule = targetModel.getModule();
        if (targetModule == null) {
          return;
        }

        for (SModuleReference language : requiredLanguages) {
          ((AbstractModule) targetModule).addUsedLanguage(language);
        }

        for (SModelReference model : requiredImports) {
          SModel modelDescriptor = SModelRepository.getInstance().getModelDescriptor(model);
          if (modelDescriptor == null) {
            continue;
          }
          SModule module = modelDescriptor.getModule();
          if (module == null || module == targetModule) {
            continue;
          }

          ((AbstractModule) targetModule).addDependency(module.getModuleReference(), false);
        }
      }
    };
  }
  public static boolean isStringOnTopOfClipboard() {
    // This method was created in accordance with TextPasteUtil.hasStringInClipboard()/.getStringFromClipboard() 
    // methods we should consider reimplementing these methods in order to iterrate over .getAllContents() collection 
    // in case first available Transferable does not support neither stringFlavor nor sNode one. 
    for (Transferable trf : CopyPasteManagerEx.getInstanceEx().getAllContents()) {
      if (trf != null) {
        for (DataFlavor nextFlavor : trf.getTransferDataFlavors()) {
          if (nextFlavor == SModelDataFlavor.stringFlavor) {
            return true;
          }
          if (nextFlavor == SModelDataFlavor.sNode) {
            return false;
          }
        }
      }
      break;
    }
    return false;
  }
