public class RenameModelDialog extends RenameDialog {
  private JCheckBox myUpdateAllReferences;
  private Project myProject;
  private EditableSModel myModelDescriptor;

  public RenameModelDialog(Project project, EditableSModel sm) throws HeadlessException {
    super(project, sm.getModelName(), "model");
    myProject = project;
    myModelDescriptor = sm;
    setTitle("Rename Model");
  }

  @Override
  protected JComponent createNorthPanel() {
    super.createNorthPanel();
    myUpdateAllReferences = new JCheckBox("Update All References");
    myUpdateAllReferences.getModel().setSelected(true);
    GridBagConstraints c = new GridBagConstraints();
    c.gridx = 0;
    c.gridy = 2;
    c.insets = new Insets(0, 8, 0, 8);
    c.anchor = GridBagConstraints.FIRST_LINE_START;
    myPanel.add(myUpdateAllReferences, c);
    return myPanel;
  }

  @Override
  protected void doRefactoringAction() {
    final String newModelName = getCurrentValue();
    if (!((SourceVersion.isName(SModelStereotype.withoutStereotype(newModelName))))) {
      setErrorText("Model name should be valid Java package");
      return;
    }
    if (!((newModelName.equals(myModelDescriptor.getModelName())))) {
      ApplicationManager.getApplication().runWriteAction(new Runnable() {
        @Override
        public void run() {
          DeleteModelHelper.deleteGeneratedFiles(myModelDescriptor);
        }
      });
      final ModelRenamer renamer = new ModelRenamer(myModelDescriptor, newModelName, !(myUpdateAllReferences.getModel().isSelected()));
      ModelAccess.instance().runWriteActionInCommand(new Runnable() {
        public void run() {
          renamer.rename();
        }
      });
      ProgressManager.getInstance().run(new Task.Modal(myProject, "Updating model usages...", false) {
        @Override
        public void run(@NotNull ProgressIndicator indicator) {
          indicator.pushState();
          indicator.setIndeterminate(true);
          try {
            ModelAccess.instance().runWriteAction(new Runnable() {
              public void run() {
                renamer.updateReferencesIfNeeded();
              }
            });
          } finally {
            indicator.popState();
          }
        }
      });
    }

    super.doRefactoringAction();
  }
}
